              source={righticon}
              initHeight="8"
              initWidth="8"
            />
          </MoreeView>
        </TouchableOpacity>
        <ButtonView>
          <TouchableOpacity
            onPress={() => {
              navigation.navigate('MyResponse');
            }}>
            <Button
              onPress={() => {
                navigation.navigate('Welcomeuser');
              }}
              style={{
                marginTop: heightPercentageToDP(3),
                width: widthPercentageToDP(93),
                marginBottom: heightPercentageToDP(10),
