import React, {useState} from 'react';
import {
  Text,
  StyleSheet,
  View,
  Image,
  ImageBackground,
  TouchableOpacity, SafeAreaView
} from 'react-native';
import {Textarea, Form} from 'native-base';
import {
  getadvive2background,
  menu,
  single,
  sexual,
  finance,
  genral,
  material,
  enterprene,
  headerView,
  botomView
} from '../../common/images';
import {
  heightPercentageToDP,
  widthPercentageToDP,
} from 'react-native-responsive-screen';
import styled from 'styled-components/native';
import ResponsiveImage from 'react-native-responsive-image';
import Button from '../../components/button';
import {Dialog} from 'react-native-simple-dialogs';
import {KeyboardAwareScrollView} from 'react-native-keyboard-aware-scrollview';
import {useNavigation, DrawerActions} from '@react-navigation/native';
import { ScrollView } from 'react-native-gesture-handler';
import Header from '../../components/header';

const GetAdvice2 = () => {
  const navigation = useNavigation();
  const [dialog, setDialog] = useState(false);

  return (
    <View>
        <Header title="Get Advice" backButton="true"/>    
        <BackgroundImage source={botomView}>
        <View  style={{ flex:1 }}>
        <ScrollView >
        <SitutionView>
          <SitutionText>
            Sending Your Sitution Anonymously for Advice
          </SitutionText>
          <ButtonSUbmit
            onPress={() => setDialog(true)}
            name={'Submit'}
            linear
          />
        </SitutionView>
        <ImagesWelcome>
          <TouchableOpacity
            onPress={() => {
              navigation.navigate('AdviceFinance');
            }}>
            <ImagesView source={finance} initHeight="130" initWidth="130" />
          </TouchableOpacity>
          <ImagesView
            source={sexual}
            initHeight="130"
            initWidth="130"
            // borderRadius={5}
          />
        </ImagesWelcome>
        <NameView>
          <Text
            style={{
              fontSize: 20,
              fontFamily: 'FuturaPT-Book',
              marginLeft: widthPercentageToDP(18),
            }}>
            Finance
          </Text>

          <Text
            style={{
              fontSize: 20,
              fontFamily: 'FuturaPT-Book',
              marginRight: widthPercentageToDP(19),
            }}>
            Sexual
          </Text>
        </NameView>
        <ImagesWelcome>
          <ImagesView source={material} initHeight="130" initWidth="130" />

          <ImagesView source={enterprene} initHeight="130" initWidth="130" />
        </ImagesWelcome>
        <NameView>
          <Text
            style={{
              fontSize: 20,
              fontFamily: 'FuturaPT-Book',
              marginLeft: widthPercentageToDP(18),
            }}>
            Marital
          </Text>
          <Text
            style={{
              fontSize: 20,
              fontFamily: 'FuturaPT-Book',
              marginRight: widthPercentageToDP(13),
            }}>
            Entrepreneurs
          </Text>
        </NameView>
        <ImagesWelcome>
          <ImagesView source={single} initHeight="130" initWidth="130" />

          <ImagesView source={genral} initHeight="130" initWidth="130" />
        </ImagesWelcome>
        <NameView>
          <Text
            style={{
              fontSize: 20,
              fontFamily: 'FuturaPT-Book',
              marginLeft: widthPercentageToDP(20),
            }}>
            Single
          </Text>
          <Text
            style={{
              fontSize: 20,
              fontFamily: 'FuturaPT-Book',
              marginRight: widthPercentageToDP(18),
            }}>
            General
          </Text>
        </NameView>
        <Dialog visible={dialog} onTouchoutside={() => setDialog(false)}>
          <View>
            <Form>
              <Textarea rowSpan={10} placeholder="100 Characters" />
            </Form>
          </View>
          <View style={{flexDirection: 'row', justifyContent: 'space-between'}}>
            <Button
              onPress={() => {
                setDialog(false);
                navigation.navigate('Welcomeuser');
              }}
              style={{
                marginTop: heightPercentageToDP(3),
                width: widthPercentageToDP(35),
              }}
              name={'Submit'}
              linear
            />
            <Button
              onPress={() => {
                navigation.navigate('Welcomeuser');
                setDialog(false);
              }}
              style={{
                marginTop: heightPercentageToDP(3),
                width: widthPercentageToDP(35),
              }}
              name={'Cancel'}
              linear
            />
          </View>
        </Dialog>
        
        </ScrollView>
        </View>
       </BackgroundImage>
      </View>
 
  );
};
const NameView = styled(View)({
  flexDirection: 'row',
  justifyContent: 'space-between',
  alignItems: 'center',
  marginTop: heightPercentageToDP(1),
});
const ImagesView = styled(ResponsiveImage)({});
const ImagesWelcome = styled(View)({
  flexDirection: 'row',
  justifyContent: 'space-between',
  alignItems: 'center',
  marginTop: heightPercentageToDP(4),
  paddingRight: 40,
  paddingLeft: 40,
});
const SitutionView = styled(View)({
  flexDirection: 'row',
  justifyContent: 'space-between',
  alignItems: 'center',
  marginTop: heightPercentageToDP(2),
  marginLeft: widthPercentageToDP(5),
});
const ButtonSUbmit = styled(Button)({
  width: widthPercentageToDP(39),
  marginRight: widthPercentageToDP(5),
});
const MenuIcon = styled(ResponsiveImage)({
  alignSelf: 'flex-end',
  marginRight: widthPercentageToDP(4),
});
const WelcomeText = styled(Text)({
  fontSize: 24,
  color: '#ffffff',
  fontWeight: '500',
  fontFamily: 'FuturaPT-Medium',
  marginTop: -heightPercentageToDP(0.1),
});
const SitutionText = styled(Text)({
  width: widthPercentageToDP(52),
  fontSize: 19,
  color: '#000',
  fontWeight: '500',
  fontFamily: 'FuturaPT-Book',

  marginTop: -heightPercentageToDP(0.1),
});
const WelcomeView = styled(View)({
  flexDirection: 'row',
  justifyContent: 'space-between',
  alignItems: 'center',
  marginTop: "-14%",
  marginLeft:8
});
const BackgroundImage = styled(ImageBackground)({
  height:'100%'
});

export default GetAdvice2;
